def drop_uninformative_objects(X, y):
    total_col = X.shape[1]
    drop_idx = []

    for i in range(len(X.index)):
        null_in_row = X.iloc[i].isnull().sum()

        if null_in_row / total_col >= 0.5:
            drop_idx.append(i)

    X_subset = X.drop(X.index[drop_idx])
    y_subset = y.drop(y.index[drop_idx])

    return X_subset, y_subset

